select `s_store_name`, `s_store_id`, sum(case when `d_day_name` = 'sunday' then `ss_sales_price` else null end) as `sun_sales`, sum(case when `d_day_name` = 'monday' then `ss_sales_price` else null end) as `mon_sales`, sum(case when `d_day_name` = 'tuesday' then `ss_sales_price` else null end) as `tue_sales`, sum(case when `d_day_name` = 'wednesday' then `ss_sales_price` else null end) as `wed_sales`, sum(case when `d_day_name` = 'thursday' then `ss_sales_price` else null end) as `thu_sales`, sum(case when `d_day_name` = 'friday' then `ss_sales_price` else null end) as `fri_sales`, sum(case when `d_day_name` = 'saturday' then `ss_sales_price` else null end) as `sat_sales`
from `date_dim`
where `d_date_sk` = `ss_sold_date_sk` and `s_store_sk` = `ss_store_sk` and `s_gmt_offset` = -5 and `d_year` = 2000
group by `s_store_name`, `s_store_id`
order by `s_store_name`, `s_store_id`, `sun_sales`, `mon_sales`, `tue_sales`, `wed_sales`, `thu_sales`, `fri_sales`, `sat_sales`
fetch next 100 rows only