select sum(`ss_quantity`)
from `store_sales`
where `s_store_sk` = `ss_store_sk` and `ss_sold_date_sk` = `d_date_sk` and `d_year` = 2000 and (`cd_demo_sk` = `ss_cdemo_sk` and `cd_marital_status` = 'm' and `cd_education_status` = '4 yr degree' and `ss_sales_price` between asymmetric 100.00 and 150.00 or `cd_demo_sk` = `ss_cdemo_sk` and `cd_marital_status` = 'd' and `cd_education_status` = '2 yr degree' and `ss_sales_price` between asymmetric 50.00 and 100.00 or `cd_demo_sk` = `ss_cdemo_sk` and `cd_marital_status` = 's' and `cd_education_status` = 'college' and `ss_sales_price` between asymmetric 150.00 and 200.00) and (`ss_addr_sk` = `ca_address_sk` and `ca_country` = 'united states' and `ca_state` in ('co', 'oh', 'tx') and `ss_net_profit` between asymmetric 0 and 2000 or `ss_addr_sk` = `ca_address_sk` and `ca_country` = 'united states' and `ca_state` in ('or', 'mn', 'ky') and `ss_net_profit` between asymmetric 150 and 3000 or `ss_addr_sk` = `ca_address_sk` and `ca_country` = 'united states' and `ca_state` in ('va', 'ca', 'ms') and `ss_net_profit` between asymmetric 50 and 25000)